%pyspark
def loadMovieNames():
    movieNames = {}
    with open('/tmp/u.item', 'r', encoding = 'latin1') as f:
        for line in f:
            fields = line.strip().split('|')
            movieNames[int(fields[0])] = fields[1]
    return movieNames

a = loadMovieNames()
nameDict = sc.broadcast(a)
